import React, { Component } from "react";
// import ImageList from "./ImageList";

const baseUrl = `https://api.flickr.com/services/rest/`;
const API_KEY = process.env.REACT_APP_API_KEY;

class Api extends Component {
  state = { items: [] };

  componentDidMount() {
    const url = `${baseUrl}?method=flickr.photos.search&api_key=${API_KEY}&per_page=10&format=json&nojsoncallback=1&extras=owner_name,description,url_o`;

    fetch(url)
      .then(response => response.json())
      .then(response => this.setState({ items: response.data }));
        // console.log(response.data);
  }

  render() {
    const posts = this.items.map((item) => (
      <div>
        <h1>{ item.photo.id }</h1>
        <span>{ item.photo.description }</span>
      </div>
    ));
    return (
      <div>
        { posts }
      </div>
    )
  }
}

export default Api;



