class Api extends Component {

    // set initial state of elements
    constructor(props) {
        super(props);
        this.state = {
            items: []
        }
    }

    // make API request
    componentDidMount() {
      this.request =
        axios
          .get(`${baseUrl}?method=${method}&api_key=${API_KEY}&format=${format}&nojsoncallback=${jsonCallback}&per_page=${pageCount}&extras=${extras}`)
          .then((result => {
            this.setState({
              items: result.data.photos.photo
            })
          }))
    }

    // handle image selection
    selectImage(selectedImage) {
        this.setState({
            selectedImage
        })
    }

    // render the app
    render() {
        const { items } = this.state;
        console.log(items);
        return (
          <div className="media-gallery">
            <h1 className="media-gallery__title">Flickr API Component</h1>
            <div className="media-gallery-thumbnails">
              {this.state.items.length ?
                this.state.items.map((item, index) =>
                  <div key={index}>
                    <img className="media-gallery-thumbnails__img" alt={item.id} src={item.url_o}/>
                    <p>{item.description._content}</p>
                  </div>

                  )
                : <div>Loading...</div>
              }
            </div>
