import React, { Component } from "react";
// import ImageList from "./ImageList";

const baseUrl = `https://api.flickr.com/services/rest/`;
const API_KEY = process.env.REACT_APP_API_KEY;

class Api extends Component {
  constructor(props) {
    super(props);

    this.state = {
      items: [],
    }
  }

  componentDidMount() {
    const url = `${baseUrl}?method=flickr.photos.getRecent&api_key=${API_KEY}&per_page=10&format=json&nojsoncallback=1&extras=owner_name,description,url_o`;

    fetch(url)
      .then(response => response.json())
      .then(data => {
        let picArray = data.photos.photo.map(pic => {
          let srcPath =
            "https://farm" +
            pic.farm +
            ".staticflickr.com/" +
            pic.server +
            "/" +
            pic.id +
            "_" +
            pic.secret +
            ".jpg";
          return <img alt="pic" src={srcPath} />;
        });
        this.setState({ items: picArray });
      }
    );
  }

  render() {
    const photos = this.state.items.map(photos => {
      return <div>{photos}</div>;
    });

    console.log('hi', {photos});

    return (
      <div>
        <span>{photos}</span>
        <span>{photos.photo.owner}</span>
      </div>
    )
  }
}

export default Api;
