import React, { Component } from "react";
import axios from "axios";
// import ImageList from "./ImageList";

const API_KEY = "5f0ca9ae5c0961037501f34373524d33";

class Api extends Component {
  state = {
    pictures: []
  };

  // API request
  componentDidMount() {
    this.serverRequest = axios
      .get(
        `https://api.flickr.com/services/rest/?method=flickr.photos.getRecent&api_key=${API_KEY}&tags=dogs&per_page=10&format=json&nojsoncallback=1`
      )
      .then(results => {
        this.setState({
          items: result.data.photos.photo,
          // items: result.data.people.getinfo,
          selectedImage: this.imageURL(result.data.photos.photo[0])
        });
      });
  }

  // assemble URL
  imageURL(item) {
    return 'http://farm' + item.farm + '.staticflickr.com/' + item.server + '/' + item.id + '_' + item.secret + '.jpg'
  }

  // handle image selection
  selectImage(selectedImage) {
    this.setState({
      selectedImage
    })
  }

